 import React, { useState } from "react";
import Exercicio6 from "./Exercicio6";

const Exer6 = () => {

  const [numbers, setNumbers] = useState ([2, 4, 6, 8, 10]);

  const numbersVerification = numbers.filter((item) => item.includes(item % 2 === 0));

  if (numbers === numbersVerification) {
    return "Esses números são pares"
  }

  return (
    <div>
      <hr></hr>
      <h2>Exer 05</h2>
      {numbersVerification.map((item) => (
        <div key={item.id}>
          <li>{"Os números pares são" + numbersVerification }</li>
        </div>
      ))}
    </div>
  );
};

export default Exer6;
