import { useState } from "react"

const Exer8 = () => {

    const [altura, setAltura] = useState("");
    const [peso, setPeso] = useState("");
    const [resultado, setResultado] = useState(null);


  const inputPeso = (evento) => {
    setPeso(evento.target.value);
  }

  const inputAltura = (evento) => {
    setAltura(evento.target.value);
  }

  const calculatorImc = setPeso / inputAltura * inputAltura


  const submitButton = () => {
    if (peso === "" || altura === "") {
      alert("Preencha os dois campos antes de enviar!");
    } else {
      setAltura("");
      setPeso("");
      alert(`Seu IMC é: ${calculatorImc} !`);
    }
  };

  return (
    <div>
      <hr></hr>

      <h2>Exer 07</h2>
      <label>Altura:
        <input type="text"
          value={altura}
          onChange={inputAltura} />
      </label>
      <p>{altura}</p>

      <br />
      <label>Peso:
        <input type="text"
          value={peso}
          onChange={inputPeso} />
      </label>
      <p>{peso} </p>

      <br />
      <button onClick={submitButton}>Enviar</button>
    </div>
  )
}

export default Exer8
